import React, { Component } from "react";
import { AddHeader } from "./add-header/product.header";
import { AddForm } from "./add-form/adding-form";
import { Book } from "./add-form/select-box-type/book";
import { Furniture } from "./add-form/select-box-type/furniture";
import { DVD } from "./add-form/select-box-type/dvd";



export class AddProduct extends Component {
    constructor() {
      super();
      this.state = {
        selectValue:'',


      };
    }
    handleDropdownChange = (e) => {
      //console.log(e.target.value)
      this.setState({ selectValue: e.target.value })
 
    }

 
    render() {
      console.log(this.state.selectValue)
      //  console.log(AddHeader)
   
      return (
        <div className="container">
         <AddHeader/>
         <AddForm maybe =  {this.showDropOptions} handleDropdownChange={this.handleDropdownChange} selectValue={this.state.selectValue}/>
       
        </div>
      );
    }
    
   
 
  
      
  }
