import React, { Component } from "react";
import { Link } from "react-router-dom";
import './products.style.css'



export class Products extends Component {
    constructor() {
      super();
      this.state = {
        Products: [
          {
            SKU:1,
            name:'disco',
             price:12,
            specific:'nice'
          },
          {
            SKU:2,
            name:'batata',
             price:12,
            specific:'nice'
          },
          {
            SKU:3,
            name:'tomate',
             price:12,
            specific:'nice'
          },
          {
            SKU:4,
            name:'stuff',
             price:12,
            specific:'nice'
          },
          {
            SKU:5,
            name:'idk',
            price:12,
            specific:'nice'
          },
        ],
        searchField:''
      };
    }
    searchChange = (e) =>{
      // this.setState({searchField:e.target.value},()=>{console.log(this.state.searchField)})
      this.setState({searchField:e.target.value})
    }
   
   
  
    render() {
      const filteredProducts = this.state.Products.filter((product)=>{
        product.name.toLowerCase().includes(this.state.searchField.toLowerCase())
      })
      return (
        <div className="App">
          <header className="header">
            <h1>Product List</h1>   
                  <button id="add-product-btn" >Add Product</button>
                  <button id="delete-product-btn">Delete Product</button>
                  <input onChange={this.searchChange} className='search' type="search" placeholder='Search Product'/>
                  <Link to ='/add'>Add</Link>
                  <hr />
          </header>
          <div className="product-list" >
            {filteredProducts.map((product)=>{
              return(
                <div className="product-container">
                  <label htmlFor={product.SKU} className="product-label">
                    <input type="checkbox" className="delete-checkbox" id={product.SKU} />
                     <h2>{product.SKU}</h2>
                     <h1>{product.name}</h1>
                     <p>{product.price} $</p>
                     <p>{product.specific}</p>
                  </label>
                </div>

              )
            })}
          </div>

          
         
        </div>
      );
    }
  }
