import React, { Component } from "react";
import { Route, Routes, Link } from "react-router-dom";
import { AddHeader } from "./add-header/product.header";
import { AddForm } from "./add-form/adding-form";
import * as TypeOfItem from './add-form/select-box-type/select-routes'





export class AddProduct extends Component {
    constructor() {
      super();
      this.state = {
        selectValue:''

      };
    }
    handleDropdownChange = (e) => {
      //console.log(e.target.value)
      this.setState({ selectValue: e.target.value })
 
    
   

    }
    renderSelectedItem = ()=>{
      const Item = TypeOfItem['Book']
    }
    render() {
    
      //console.log(TypeOfItem.Book)
     // console.log(AddHeader)
   
      return (
        <div className="container">
         <AddHeader/>
         <AddForm  handleDropdownChange={this.handleDropdownChange} selectValue={this.state.selectValue}/>
          {this.renderSelectedItem}
        </div>
      );
    }
 
  
      
  }
