import React, { Component } from "react";
import { Header } from "./header/product.header";
import { ProductsList } from "./products/products.list";
import axios from 'axios'





export class Products extends Component {
    constructor() {
      super();
      this.state = {
        Products: [],
        searchField:'',
        itemChecked:{}
      };
    }
    checkedBox = (id, e) => {
      let itemChecked = this.state.itemChecked;
      itemChecked[id] = e.target.checked; //passes true of false
      this.setState({ itemChecked });
      
    
    };
    componentDidMount() {
      axios.get("http://localhost:5000/product").then((res) => {
        this.setState({ Products: res.data });
      });
    }
  
  
    render() {
      const filteredProducts = this.state.Products.filter((product)=>{
        return product.name.toLowerCase().includes(this.state.searchField.toLowerCase())
        
      })
      console.log(this.state.Products)
      return (
        <div className="App container">
          <Header searchChange={(e) => this.setState({ searchField: e.target.value })} />
          <div className="product-list" >
          <ProductsList products = {filteredProducts} isChecked={this.checkedBox}/>
          </div>
          
        </div>
      );
    }
  }
