import React, { Component } from "react";
import { AddHeader } from "./add-header/product.header";
import { AddForm } from "./add-form/adding-form";
import axios from 'axios'



export class AddProduct extends Component {
    constructor() {
      super();
      this.state = {
        selectValue:'',
          SKU:'',
          name:'',
          price:'',
        

      };
    }
    handleDropdownChange = (e) => {
      //console.log(e.target.value)
      this.setState({ selectValue: e.target.value })
 
    }
    handleSubmit = (event) =>{
      event.preventDefault();
      this.setState(
        {
          SKU: event.target.SKU.value,
          name: event.target.name.value,
          price: event.target.price.value,
          
        })
        // () => axios.post("http://localhost:5000/add", this.state.SKU) 
        // in place of this you can console.log(this.state and see the object we sending)
        console.log(this.state)
    };
    

 
    render() {
      console.log(this.state.selectValue)
      //  console.log(AddHeader)
   
      return (
        <div className="container">
         <AddHeader/>
         <AddForm handleSubmit={this.handleSubmit} handleDropdownChange={this.handleDropdownChange} selectValue={this.state.selectValue}/>
       
        </div>
      );
    }
    
   
 
  
      
  }
