import React, { Component }  from "react";
import { AddHeader } from "./add-header/product.header";
import { AddForm } from "./add-form/adding-form";
import { httpAddProduct } from "../request";
import { Navigate} from 'react-router-dom'



export class AddProduct extends Component {

    constructor() {
      super();
      this.state = {
        selectValue:'',
        formData:{},
        formId:"product_form",
        didSubmit:"false"

          // SKU:'',
          // name:'',
          // price:'',
          // height:'',
          // width:'',
          // length:'',
          // weight:'',
          // size:''
      };
    }
    
    handleDropdownChange = (e) => {
      //console.log(e.target.value)
      this.setState({ selectValue: e.target.value })
 
    }

    // thisFunc = async ()=>{
    //   await axios.post("http://localhost:5000/add", this.state)
    // }

    handleSubmit = async (event) =>{
     
      event.preventDefault();
      const targets = event.target
     
      const data = this.state.formData
      for (let target of targets){
        if(target.name === ''){
          console.log(target.name)
        }else{
          // console.log(target.name,target.value)
          data[target.name] = target.value
        }
      
      }
      
      console.log('first',this.state.formData)
      await httpAddProduct (this.state.formData)
      this.setState({
        didSubmit:true
      })

    
      // console.log('second',this.state.formData)
      // console.log(JSON.stringify(this.state.formData))    
      // for (let target of targets){
      //   const name = target.name
      //   const value = target.value
      //   const data = this.state.formData
      //   data[name] = value
      
      // }
    
    }; 
    
      
     
     
      // this.setState(
      //   {
      //     SKU: event.target.SKU.value,
      //     name: event.target.name.value,
      //     price: event.target.price.value,  
      //     size:event.target.size.value
          
      //   },
      //    () => axios.post("http://localhost:5000/add", this.state) 
      //   // in place of this you can console.log(this.state and see the object we sending)
      // )
      // console.log(this.state.formData)
      // // this.thisFunc()
   
    

 
    render() {
      if(this.state.didSubmit){
        return <Navigate to='/'/>
      }
   
      return (
        <div className="container">
         <AddHeader formId = {this.state.formId} />
         <AddForm formId = {this.state.formId} handleSubmit={this.handleSubmit} handleDropdownChange={this.handleDropdownChange} selectValue={this.state.selectValue}/>
       
        </div>
      );
    }
    
   
 
  
      
  }
