  import React, { Component } from "react";
import { Header } from "./header/product.header";
import { ProductsList } from "./products/products.list";
import axios from 'axios'



export class Products extends Component {
    constructor() {
      super();
      this.state = {
        Products: [],
        searchField:'',
        itemChecked:{},
        deleteList: [],
      };
    }
    checkedBox = (id, e) => {
      let itemChecked = this.state.itemChecked;
      itemChecked[id] = e.target.checked; //passes true of false
     this.setState({ itemChecked },(()=>console.log(itemChecked)));
      // this.setState({ itemChecked });
      
    
    };
    deleteProduct = () => {
      let itemChecked = this.state.itemChecked;
      const items = Object.entries(itemChecked);
      const { deleteList } = this.state;
     
      console.log(this.state.Products)
      // items.map((item)=>{
      //   return item[1] === true ?  deleteList.push(item) : console.log(item)
      // })
      for (let item of items) {
        if (item[1] === true) {
          deleteList.push(item);
        }
      }
      console.log(deleteList)
      axios.delete("http://localhost:5000/product", {
        data: this.state.deleteList
      })
      const item = deleteList.map(item => !this.state.Products.SKU.includes(item[0]))
      // for (let product of this.state.Products){
      //   for ()
      // }
      // const filtered = this.state.Products.filter(product=> deleteList.includes(product))
      // console.log(filtered )
      // return this.setState({Products:filtered })
        // const list = [];
        // for (let item of deleteList) {
        //   list.push(
        //     this.state.Products.filter((product) => product.SKU !== item[0])
        //   );
        // }
      
    };

    componentDidMount() {
      axios.get("http://localhost:5000/product").then((res) => {
        this.setState({ Products: res.data });
      });
    }
  
  
    render() {
      const filteredProducts = this.state.Products.filter((product)=>{
        return product.name.toLowerCase().includes(this.state.searchField.toLowerCase())
        
      })
      //console.log(this.state.Products)
      return (
        <div className="App container">
          <Header  itemChecked={this.state.itemChecked}  deleteProduct={this.deleteProduct}  searchChange={(e) => this.setState({ searchField: e.target.value })} />
          <div className="product-list" >
          <ProductsList
                
                 products = {filteredProducts} 
                 isChecked={this.checkedBox}/>
          </div>
          
        </div>
      );
    }
  }
